    isNewVersion: function(){
        // **********
        // HFR4K_GM
        // Check if the current version of the script is newer than the one last used by the user
        // Return boolean
        // **********

        // Ugly but finally does the job...
        var result = false;
        var done = false;
        var localArr = version.split('.');
        LocalMPStorage.datas.hfr4k.lastVersion.split('.').forEach(function(n, i){
            if(!done){
                if (parseInt(n) < parseInt(localArr[i])){
                    result = true;
                    done = true;
                } else if (parseInt(n) > parseInt(localArr[i])){
                    done = true;
                }
            }

        });
        return result;
    },
