                                        var newImgBlocHide = document.createElement('img');
                                        newImgBlocHide.setAttribute('title', 'Ajouter aux super favoris/Ne plus masquer');
                                        newImgBlocHide.setAttribute('src', HFRGMUtils.icons.hide);


